import React from "react";
import "./Catalogue.css";

const books = [
  { title: "The Alchemist", author: "Paulo Coelho", img: "https://m.media-amazon.com/images/I/71aFt4+OTOL._AC_UF1000,1000_QL80_.jpg" },
  { title: "Atomic Habits", author: "James Clear", img: "https://m.media-amazon.com/images/I/91bYsX41DVL._AC_UF1000,1000_QL80_.jpg" },
  { title: "Rich Dad Poor Dad", author: "Robert Kiyosaki", img: "https://m.media-amazon.com/images/I/81bsw6fnUiL._AC_UF1000,1000_QL80_.jpg" },
  { title: "Wings of Fire", author: "APJ Abdul Kalam", img: "https://m.media-amazon.com/images/I/71KKZlVjbwL._AC_UF1000,1000_QL80_.jpg" }
];

export default function Catalogue() {
  return (
    <div className="container mt-5">
      <h2 className="text-center fw-bold mb-4">ðŸ“– Book Catalogue</h2>

      <div className="row justify-content-center">
        {books.map((book, index) => (
          <div className="col-md-3" key={index}>
            <div className="card book-card shadow-lg p-3 mb-4">
              <img src={book.img} className="card-img-top book-img" alt="book-cover" />
              <div className="card-body text-center">
                <h5 className="card-title fw-bold">{book.title}</h5>
                <p className="card-text text-muted">by {book.author}</p>
                <button className="btn btn-outline-primary w-100 mt-2">View Details</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
